mapper.select:
SELECT
    *
FROM
    cfl_allowed_server
WHERE 
    svc_nm = #{allowedServer.serviceName}
    AND tnnt_id = #{allowedServer.tenantId}
    AND svr_ip = #{allowedServer.serverIp}

mapper.insert:
INSERT INTO
    cfl_allowed_server (svr_ip, tnnt_id, svr_nm, svc_nm)
VALUE
    (#{allowedServer.serverIp}
    , #{allowedServer.tenantId}
    , #{allowedServer.serverName}
    , #{allowedServer.serviceName})

mapper.update:
UPDATE
    cfl_allowed_server
SET
    svr_ip = #{allowedServer.serverIp}
    , tnnt_id = #{allowedServer.tenantId}
    , svr_nm = #{allowedServer.serverName}
    , svc_nm = #{allowedServer.serviceName}
WHERE
    svr_ip = #{originalIp}
