mapper.select:
SELECT
    *
FROM
    cfl_authority
WHERE
    svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}
    AND auth_id = #{authorityId}

mapper.insert:
INSERT INTO
    cfl_authority (auth_id, auth_nm, auth_type, tnnt_id, svc_nm)
VALUE
    (#{authorityId}
    , #{authorityName}
    , #{authorityType}
    , #{tenantId}
    , #{serviceName})

mapper.update:
UPDATE
    cfl_authority
SET
    auth_id = #{authority.authorityId}
    , tnnt_id = #{authority.tenantId}
    , auth_nm = #{authority.authorityName}
    , svc_nm = #{authority.serviceName}
    , auth_type = #{authority.authorityType}
WHERE
    svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}
    AND auth_id = #{authorityId}

mapper.delete:
DELETE FROM
    cfl_authority
WHERE
    svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}
    AND auth_id = #{authorityId}
