mapper.select:
select *
from authority
where svc_nm = #{authority.serviceName} and tnnt_id = #{authority.tenantId}

select *
from user
where svc_nm = #{authority.serviceName} and tnnt_id = #{authority.tenantId} and auth_id = #{authority.authorityId}

mapper.update:
update authority
set auth_id = #{authority.authorityId}, tnnt_id = #{allowedServer.tenantId},
    auth_nm = #{authority.authorityName}, svc_nm = #{allowedServer.serviceName}),
    auth_type = #{authority.authorityType}
where auth_seq = #{authority.authoritySequence}
