mapper.select:
SELECT
    *
FROM
    cfl_object
WHERE
    svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}
    AND objt_id = #{objectId}

mapper.insert:
INSERT INTO
    cfl_object (objt_id, objt_nm, tnnt_id, svc_nm)
VALUE
    (#{objectId}
    , #{objectName}
    , #{tenantId}
    , #{serviceName})

mapper.update:
UPDATE
    cfl_object
SET
    objt_id = #{objectId}
    , tnnt_id = #{tenantId}
    , objt_nm = #{objectName}
    , svc_nm = #{serviceName})
WHERE
    objt_seq = #{objectSequence}
