mapper.select:
SELECT
    *
FROM
    cfl_object
WHERE
    svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}
    AND objt_id = #{objectId}

mapper.insert:
INSERT INTO
    cfl_object (objt_id, objt_nm, objt_type, tnnt_id, svc_nm)
VALUE
    (#{objectId}
    , #{objectName}
    , #{objectType}
    , #{tenantId}
    , #{serviceName})

mapper.update:
UPDATE
    cfl_object
SET
    objt_id = #{object.objectId}
    , tnnt_id = #{object.tenantId}
    , objt_type = #{object.objectType}
    , objt_nm = #{object.objectName}
    , svc_nm = #{object.serviceName}
WHERE
    svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}
    AND objt_id = #{objectId}

mapper.delete:
DELETE FROM
    cfl_object
WHERE
    svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}
    AND objt_id = #{objectId}
