mapper.select:
SELECT
    *
FROM
    cfl_object
WHERE
    svc_nm = #{object.serviceName}
    AND tnnt_id = #{object.tenantId}
    AND objt_id = #{object.objectId}

mapper.insert:
INSERT INTO
    cfl_object (objt_id, objt_nm, tnnt_id, svc_nm)
VALUE
    (#{object.objectId}
    , #{object.objectName}
    , #{object.tenantId}
    , #{object.serviceName})

mapper.update:
UPDATE
    cfl_object
SET
    objt_id = #{object.objectId}
    , tnnt_id = #{object.tenantId}
    , objt_nm = #{object.objectName}
    , svc_nm = #{object.serviceName})
WHERE
    objt_seq = #{object.objectSequence}
