mapper.insert:
INSERT INTO
    cfl_code (cd_id, cd_nm, cd_desc, use_yn, ord, mlng_cd, prnt_cd_id, tnnt_id, svc_nm)
VALUE
    (#{codeId}
    , #{codeName}
    , #{codeDescription}
    , #{isUsed}
    , #{order}
    , #{multiLanguageCode}
    , #{parentCodeId}
    , #{tenantId}
    , #{serviceName})

mapper.update:
UPDATE
    cfl_code
SET
    cd_nm = #{codeName}
    , cd_desc = #{codeDescription}
    , use_yn = #{isUsed}
    , ord = #{order}
    , mlng_cd = #{multiLanguageCode}
    , prnt_cd_id = #{parentCodeId}
    , tnnt_id = #{tenantId}
    , svc_nm = #{serviceName}
WHERE
    cd_id = #{codeId}
    AND svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}

mapper.delete:
DELETE FROM
    cfl_code
WHERE
    cd_id = #{codeId}
    AND svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}

mapper.select:
SELECT
    *
FROM
    cfl_code
WHERE
    cd_id = #{codeId}
    AND svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}
