mapper.insert:
INSERT INTO
    cfl_code (cd_id, cd_nm, cd_desc, use_yn, ord, mlng_cd, prnt_cd_id, tnnt_id, svc_nm)
VALUE
    (#{code.codeId}
    , #{code.codeName}
    , #{code.codeDescription}
    , #{code.isUsed}
    , #{code.order}
    , #{code.multiLanguageCode}
    , #{code.parentCodeId}
    , #{code.tenantId}
    , #{code.serviceName})

mapper.update:
UPDATE
    cfl_code
SET
    cd_nm = #{code.codeName}
    , cd_desc = #{code.codeDescription}
    , use_yn = #{code.isUsed}
    , ord = #{code.order}
    , mlng_cd = #{code.multiLanguageCode}
    , tnnt_id = #{code.tenantId}
    , svc_nm = #{code.serviceName}
WHERE
    cd_id = #{code.codeId}
    AND prnt_cd_id = #{code.parentCodeId}
    AND svc_nm = #{code.serviceName}
    AND tnnt_id = #{code.tenantId}

mapper.delete:
DELETE FROM
    cfl_code
WHERE
    cd_id = #{code.codeId}
    AND prnt_cd_id = #{code.parentCodeId}
    AND svc_nm = #{code.serviceName}
    AND tnnt_id = #{code.tenantId}

mapper.select:
SELECT
    *
FROM
    cfl_code
WHERE
    cd_id = #{code.codeId}
    AND prnt_cd_id = #{code.parentCodeId}
    AND svc_nm = #{code.serviceName}
    AND tnnt_id = #{code.tenantId}
