mapper.insert:
INSERT INTO
    cfl_code (cd_id, cd_nm, cd_desc, use_yn, ord, mlng_cd, tnnt_id, svc_nm)
VALUE
    (#{code.codeId}
    , #{code.codeName}
    , #{code.codeDescription}
    , #{code.isUsed}
    , #{code.order}
    , #{code.multiLanguageCode}
    , #{code.tenantId}
    , #{code.serviceName})

mapper.select:
SELECT 
       count(1) AS cnt
  FROM cfl_code_tree
 WHERE tre_id = #{treeId}
   AND root_cd_seq = #{rootCodeSequence}

mapper.update:
UPDATE
    cfl_code
SET
    cd_nm = #{code.codeName}
    , cd_desc = #{code.codeDescription}
    , use_yn = #{code.isUsed}
    , ord = #{code.order}
    , mlng_cd = #{code.multiLanguageCode}
    , tnnt_id = #{code.tenantId}
    , svc_nm = #{code.serviceName}
WHERE
    cd_seq = #{code.codeSequence}
    AND svc_nm = #{code.serviceName}
    AND tnnt_id = #{code.tenantId}

mapper.delete:
DELETE FROM
    cfl_code
WHERE
    cd_seq = #{code.codeSequence}
    AND svc_nm = #{code.serviceName}
    AND tnnt_id = #{code.tenantId}

mapper.select:
SELECT
    *
FROM
    cfl_code
WHERE
    cd_seq = #{code.codeSequence}
    AND svc_nm = #{code.serviceName}
    AND tnnt_id = #{code.tenantId}
