mapper.insert:
INSERT INTO
    cfl_allowed_server (svr_ip, svr_nm, svc_nm)
VALUE
    (#{server.serverIp}
    , #{server.serverName}
    , #{server.serviceName})

mapper.update:
UPDATE
    cfl_allowed_server
SET
    svr_ip = #{server.serverIp}
    , svr_nm = #{server.serverName}
    , svc_nm = #{server.serviceName}
WHERE
    svc_nm = #{server.serviceName}
    AND svr_ip = #{server.serverIp}

mapper.delete:
DELETE FROM
    cfl_allowed_server
WHERE
    svc_nm = #{server.serviceName}
    AND svr_ip = #{server.serverIp}
