mapper.select:
SELECT
    *
FROM
    cfl_allowed_server
WHERE 
    svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}
    AND svr_ip = #{serverIp}

mapper.insert:
INSERT INTO
    cfl_allowed_server (svr_ip, tnnt_id, svr_nm, svc_nm)
VALUE
    (#{serverIp}
    , #{tenantId}
    , #{serverName}
    , #{serviceName})

mapper.update:
UPDATE
    cfl_allowed_server
SET
    svr_ip = #{server.serverIp}
    , tnnt_id = #{server.tenantId}
    , svr_nm = #{server.serverName}
    , svc_nm = #{server.serviceName}
WHERE
    svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}
    AND svr_ip = #{serverIp}

mapper.delete:
DELETE FROM
    cfl_allowed_server
WHERE
    svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}
    AND svr_ip = #{serverIp}
