mapper.insert:
INSERT INTO
    cfl_user (user_id, user_type, tnnt_id, user_seq, svc_nm)
VALUE
    (#{user.userId}
    , #{user.userType}
    , #{user.tenantId}
    , #{user.userSequence}
    , #{user.serviceName})

mapper.update:
UPDATE
    cfl_user
SET
    user_id = #{user.userId}
    , user_type = #{user.userType}
    , tnnt_id = #{user.tenantId}
    , user_seq = #{user.userSequence}
    , svc_nm = #{user.serviceName}
WHERE
    user_seq = #{user.userSequence}

mapper.delete:
DELETE FROM
    cfl_user
WHERE
    user_id = #{user.userId}
    AND svc_nm = #{user.serviceName}
    AND tnnt_id = #{user.tenantId}

mapper.select:
SELECT
    *
FROM
    cfl_user
WHERE
    user_id = #{user.userId}
    AND svc_nm = #{user.serviceName}
    AND tnnt_id = #{user.tenantId}
