mapper.select:
SELECT
    *
FROM
    cfl_user
WHERE
    user_id = #{user.userId}
    AND svc_nm = #{serviceName}
    AND tnnt_id = #{tenantId}

mapper.insert:
INSERT INTO
    cfl_user (user_id, user_type, tnnt_id, user_seq, svc_nm)
VALUE
    (#{userId}
    , #{userType}
    , #{tenantId}
    , #{userSequence}
    , #{serviceName})

mapper.update:
UPDATE
    cfl_user
SET
    user_id = #{user.userId}
    , user_type = #{user.userType}
    , tnnt_id = #{user.tenantId}
    , user_seq = #{user.userSequence}
    , svc_nm = #{user.serviceName}

mapper.delete:
DELETE FROM
    cfl_user
WHERE
    user_id = #{user.userId}
    AND svc_name = #{user.serviceName}
    AND tnnt_id = #{user.tenantId}
